class Game extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
      squares: Array(9).fill(null),
      position: [null, null],
    };

    this.commands = {
      place: (args) => {
        console.log("Place: " + args);

        // check if arguments ar valid
        if (args.length !== 3) throw Error("Place: Invalid arguments");
        let [x, y, dir] = args;
        if (
          isNaN(x) ||
          isNaN(y) ||
          !["NORTH", "SOUTH", "EAST", "WEST"].includes(dir.toUpperCase())
        )
          throw Error("Place: Invalid arguments");

        const squares = Array(9).fill(null);
        squares[5 * y + x] = "X";
        this.setState({ squares: squares });
      },
      move: (args) => {
        console.log("Move: " + args);
        const squares = Array(9).fill(null);
        squares[7] = "X";
        this.setState({ squares: squares });
      },
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.processInput(this.state.value);
  }

  processInput(input) {
    try {
      let command = input.split("(")[0];
      if (command === input) throw new Error("Invalid command syntax");

      let args = input.split("(")[1].split(")")[0];
      if (args === input) args = [];
      else args = args.split(",");

      if (command in this.commands) {
        let fn = this.commands[command];
        // alert(`Command ${command} with arguments ${args}`);
        fn(args);
      } else {
        throw new Error(`Command ${command} not found!`);
      }
    } catch (e) {
      alert(e.message);
      console.log(e);
    }
  }

  renderSquare(i) {
    return <Square value={this.state.squares[i]} />;
  }

  render() {
    return (
      <div className="flex-col">
        <div>
          <div>
            <div className="board-row">
              {this.renderSquare(1)}
              {this.renderSquare(2)}
              {this.renderSquare(3)}
            </div>
            <div className="board-row">
              {this.renderSquare(4)}
              {this.renderSquare(5)}
              {this.renderSquare(6)}
            </div>
            <div className="board-row">
              {this.renderSquare(7)}
              {this.renderSquare(8)}
              {this.renderSquare(9)}
            </div>
          </div>
        </div>
        <div>
          {/* <Console
            handleChange={this.handleChange}
            handleSubmit={this.handleSubmit}
            value={this.state.value}
          /> */}
          <form onSubmit={this.handleSubmit}>
            <label>
              Robo:~ $
              <input
                type="text"
                value={this.value}
                onChange={this.handleChange}
              />
            </label>
            <input type="submit" value="Run" />
          </form>
        </div>
      </div>
    );
  }
}

