class Game extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
      squares: Array(9).fill(null),
      pos: [null, null],
      dir: null,
    };

    this.commands = {
      place: (args) => {
        // check if arguments ar valid
        if (args.length !== 3) throw Error("Place: Should have 3 arguments");
        let [x, y, dir] = args;
        if (
          isNaN(x) ||
          isNaN(y) ||
          !["NORTH", "SOUTH", "EAST", "WEST"].includes(dir.toUpperCase())
        )
          throw Error("Place: Invalid arguments");
        else [x, y, dir] = [+x, +y, dir.toUpperCase()];

        // check if position is within bounds
        if (x < 0 || x > 4 || y < 0 || y > 4)
          throw Error(`Place: Position ${x}, ${y} out of bounds`);

        // update state
        const squares = Array(9).fill(null);
        let i = 5 * y + x;
        squares[i] = "X";

        this.setState({
          squares: squares,
          pos: [x, y],
          dir: dir.toUpperCase(),
        });
      },
      move: (args) => {
        if (args.length !== 0) throw Error("Move: Should have no arguments");

        const squares = Array(9).fill(null);
        squares[2] = "X";
        this.setState({ squares: squares });
      },
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.processInput(this.state.value);
  }

  processInput(input) {
    try {
      let command = input.split("(")[0];
      if (command === input) throw new Error("Invalid command syntax");

      let args = input.split("(")[1].split(")")[0];
      if (args === input) args = [];
      else args = args.split(",");

      if (command in this.commands) {
        let fn = this.commands[command];
        // alert(`Command ${command} with arguments ${args}`);
        fn(args);
      } else {
        throw new Error(`Command ${command} not found!`);
      }
    } catch (e) {
      alert(e.message);
      console.log(e);
    }
  }

  renderSquare(i) {
    return <Square value={this.state.squares[i]} />;
  }

  render() {
    return (
      <div className="flex-col">
        <div>
          <div>
            <div className="flex">
              {this.renderSquare(20)}
              {this.renderSquare(21)}
              {this.renderSquare(22)}
              {this.renderSquare(23)}
              {this.renderSquare(24)}
            </div>
            <div className="flex">
              {this.renderSquare(15)}
              {this.renderSquare(16)}
              {this.renderSquare(17)}
              {this.renderSquare(18)}
              {this.renderSquare(19)}
            </div>
            <div className="flex">
              {this.renderSquare(10)}
              {this.renderSquare(11)}
              {this.renderSquare(12)}
              {this.renderSquare(13)}
              {this.renderSquare(14)}
            </div>
            <div className="flex">
              {this.renderSquare(5)}
              {this.renderSquare(6)}
              {this.renderSquare(7)}
              {this.renderSquare(8)}
              {this.renderSquare(9)}
            </div>
            <div className="flex">
              {this.renderSquare(0)}
              {this.renderSquare(1)}
              {this.renderSquare(2)}
              {this.renderSquare(3)}
              {this.renderSquare(4)}
            </div>
          </div>
        </div>
        <div className="py-">
          {/* <Console
            handleChange={this.handleChange}
            handleSubmit={this.handleSubmit}
            value={this.state.value}
          /> */}
          <form onSubmit={this.handleSubmit}>
            <label>
              Robo:~ $
              <input
                type="text"
                value={this.value}
                onChange={this.handleChange}
              />
            </label>
            <input type="submit" value="Run" />
          </form>
        </div>
      </div>
    );
  }
}

